toStr 0 = ""

toStr x | length l == 2 = getStr (head l) ++ toStr ( l !! 1) 
        | length l == 3 && (l!!1 + l!!2 == 0) = toStr(head l) ++ "hundred"
        | length l == 3 = toStr(head l) ++ "hundred" ++ "and" ++ (toStr (l!!1 * 10 + l!!2))
        | length l == 4 = toStr(head l) ++ "thousand" ++ toStr (l!!1*100 + l!!2 * 10 + l!!3)
            where l = intolist x

