--Euler 21
pfactors x = pfactors1 x 1
				where pfactors1 x n	| n > (x `div` 2)	= []
									| x `mod` n == 0 = n : pfactors1 x (n+1)
									| otherwise = pfactors1 x (n+1)
sumprop x = sum $ pfactors x

euler21 = [(a,b) | a <- [1..10000], b <- [1..10000], (sumprop a) == b && (sumprop b) == a && (a /= b) ]


