--Euler 94

triArea a b c = sqrt $ s*(s-a)*(s-b)*(s-c)
		  where s = (a+b+c) / 2

euler94 = [((triArea a b c),(a,b,c))| a <- [1..k], b <- [1..k], c <- [1..k], a + b + c <= k, (a + b < c || a + c < b || c + b < a)]
		where 	k = 1000000000
		
		
		
		
		
		
--euler 282

ack m n   | m == 0 = n+1
          | m > 0 && n == 0 = ack (m-1) 1
          | m > 0 && n > 0 = ack (m-1) (ack m (n-1))













    
